import React, { useEffect, useRef, useState } from 'react';

import style from './style.module.scss';
import CreateToDo from './components/CreateToDo';
import ToDo from './components/ToDo';
import Background from './components/Background';
import { ReactComponent as Arrow } from './assets/arrow.svg';

const App = () => {
  const [toDoList, setToDoList] = useState(localStorage.getItem('ToDos') ? JSON.parse(localStorage.getItem('ToDos')) : []);
  const [inputToDoName, setInputToDoName] = useState('');
  const refHeader = useRef(null);
  // DAy JS /DATE FNS
  useEffect(() => {
    localStorage.setItem('ToDos', JSON.stringify(toDoList));
  }, [toDoList]);

  const executeScroll = () => refHeader.current.scrollIntoView();

  const getString = (str) => {
    str = str.split(', ');
    const arr = [];
    str.forEach((e, i) => {
      if ((i + 1) % 2 === 0) {
        arr.push(e.substr(1, e.length));
      }
    });
    return arr;
  };

  const getFibanacci = (num) => {
    let f1 = 1;
    let f2 = 1;
    // eslint-disable-next-line no-plusplus
    for (let i = 3; i <= num; i++) {
      const f3 = f1 + f2;
      f1 = f2;
      f2 = f3;
    }
    return f2;
  };
  const func = (str, num) => {
    const slicedString = new Promise((resolve) => {
      setTimeout(() => {
        resolve(getString(str));
      }, 300);
    });
    const fibanacci = new Promise((resolve) => {
      resolve(getFibanacci(num));
    });
    console.log(slicedString, fibanacci);
  };

  func('яблоко, слон, дуб, лес', 13);

  useEffect(() => {
    func('яблоко, слон, дуб, лес', 13);
  }, []);
  return (
    <div
      className={style.main}
    >
      <Arrow
        className={style.arrow}
        role="presentation"
        onClick={executeScroll}
      />
      <Background />
      <header ref={refHeader}>
        <span>ToDo list</span>
      </header>
      <body
        className={style.authorized}
      >
        <div className={style.todoBlock}>
          <div className={style.createToDoBLock}>
            <CreateToDo
              inputToDoName={inputToDoName}
              setInputToDoName={setInputToDoName}
              toDoList={toDoList}
            />
          </div>
          <div className={style.toDoList}>
            {toDoList.map(e => (
              <ToDo
                key={e.id}
                e={e}
                toDoList={toDoList}
                setToDoList={setToDoList}
              />
            ))}
          </div>
        </div>
      </body>
    </div>
  );
};

export default App;
