import React, { useState } from 'react';

import style from './style.module.scss';

const App = () => {
  const [userData, setUserData] = useState({
    name: localStorage.getItem('userName') ? localStorage.getItem('userName') : '',
    toDosCount: localStorage.getItem('todosCount') ? localStorage.getItem('todosCount') : 0,
    toDos: [],
  });
  const [inputUserName, setInputUserName] = useState('');
  const [inputToDoName, setInputToDoName] = useState('');
  // const [toDoData, setToDoData] = useState({});
  const createUser = () => {
    if (inputUserName) {
      let userNameFirstLetterUpperCase = inputUserName;
      inputUserName.split('').forEach((e, i) => {
        if (i === 0 || inputUserName[i - 1] === ' ') {
          userNameFirstLetterUpperCase = userNameFirstLetterUpperCase.substring(0, i) + e.toUpperCase()
          + userNameFirstLetterUpperCase.substring(i + 1);
        }
      });
      localStorage.setItem('userName', userNameFirstLetterUpperCase);
      setUserData({
        name: userNameFirstLetterUpperCase,
        todosCount: userData.todosCount,
        toDos: userData.toDos,
      });
    }
  };
  // localStorage.removeItem('ToDos');
  const createToDo = () => {
    if (inputToDoName) {
      const date = new Date();
      const todo = {
        name: inputToDoName,
        time: `${date.getHours()}:${date.getMinutes() > 10
          ? date.getMinutes()
          : `0${date.getMinutes()}`}`,
      };
      userData.toDos.push(todo);
      setUserData({
        name: userData.name,
        toDos: userData.toDos,
        toDosCount: userData.toDosCount + 1,
      });
      localStorage.setItem('ToDos', JSON.stringify(userData.toDos));
      setInputToDoName('');
    }
  };
  const deleteToDo = (e) => {
    setUserData({
      name: userData.name,
      toDosCount: userData.toDosCount - 1,
      toDos: userData.toDos.filter(todo => todo.name !== e.name),
    });
  };
  return (
    <div className={style.main}>
      <header>
        <h1>{`${userData.name} ToDo list`}</h1>
      </header>
      <body
        className={userData.name === ''
          ? style.unauthorized
          : style.authorized}
      >
        {userData.name === '' ? (
          <div className={style.unauthorizedBlock}>
            <form action="#">
              <input
                type="text"
                placeholder="Set username"
                value={inputUserName}
                required
                onChange={e => setInputUserName(e.target.value)}
              />
              <button
                onClick={createUser}
                type="submit"
              >
                Submit
              </button>
            </form>
          </div>
        ) : (
          <div className={style.authorizedBlock}>
            <div className={style.createToDoBLock}>
              <form action="#">
                <input
                  type="text"
                  placeholder="Write what to do"
                  value={inputToDoName}
                  required
                  onChange={e => setInputToDoName(e.target.value)}
                />
                <button
                  onClick={createToDo}
                  type="submit"
                >
                  Create
                </button>
              </form>
            </div>
            {userData.toDos.map(e => (
              <div>
                <h4>{e.name}</h4>
                <span>{e.time}</span>
                <button
                  type="button"
                  onClick={() => deleteToDo(e)}
                >
                  X
                </button>
              </div>
            ))}
          </div>
        )}
      </body>
    </div>
  );
};

export default App;
