import React from 'react';

import styles from './styles.module.scss';

const Hint = ({ tittle, description, isShowed }) => (
  isShowed && (
    <div
      className={styles.mainBlock}
    >
      <h3>{tittle}</h3>
      <span>{description}</span>
    </div>
  )
);

export default Hint;
