import React from 'react';

import style from './style.module.scss';

const ToDo = ({ e, toDoList, setToDoList }) => {
  const deleteToDo = () => {
    setToDoList(toDoList.filter(todo => todo.id !== e.id));
  };
  const isActive = 1;
  return (
    <div className={`${style.toDo} ${isActive === 0 ? style.inProgress : isActive === 1 ? style.isFinished : ''}`}>
      <input type="checkbox" />
      <h4>{e.name}</h4>
      <textarea
        name="description"
        id={e.id}
        cols="30"
        rows="10"
        placeholder="description"
        onChange={value => setToDoList(prev => [...prev, value])}
      />
      <span>{e.time}</span>
      <button
        type="button"
        onClick={() => deleteToDo(e)}
      >
        X
      </button>
    </div>
  );
};

export default ToDo;
