import React from 'react';

import style from './style.module.scss';

import { ReactComponent as Check } from '../../assets/check.svg';

const CheckBox = ({ isActive, setIsActive, toDoList }) => {
  const newArr = () => {
    const arr = [];
    toDoList.forEach((element) => {
      if (element.id === e.id) {
        element.isActive = true;
        arr.push(element);
      } else {
        arr.push(element);
      }
    });
    return arr;
  };
  return (
    <div
      role="presentation"
      onClick={() => setIsActive(!isActive)}
      className={style.box}
    >
      {isActive && (<Check className={style.check} />)}
    </div>
  );
};

export default CheckBox;
