import React, { useEffect, useRef, useState } from 'react';

import styles from './styles.module.scss';

const Hint = ({
  title,
  description,
  children,
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [childPostion, setChildPosition] = useState({});
  const childRef = useRef();
  const showHint = () => {
    setIsVisible(true);
  };
  const hideHint = () => {
    setIsVisible(false);
  };

  useEffect(() => {
    if (childRef.current) {
      setChildPosition(childRef.current.getBoundingClientRect().DOM);
    }
  }, []);
  console.log(childPostion);
  return (
    <div
      className={styles.mainBlock}
      onMouseEnter={showHint}
      onMouseLeave={hideHint}
    >
      <div ref={childRef}>{children}</div>
      {isVisible && (
        <div className={styles.tooltipBlock}>
          <h3>{title}</h3>
          <span>{description}</span>
        </div>
      )}
    </div>
  );
};

export default Hint;
