import React, {useRef, useState} from 'react';

import style from './style.module.scss';
import CreateToDo from './components/CreateToDo';
import ToDo from './components/ToDo';

const App = () => {
  const [toDoList, setToDoList] = useState(localStorage.getItem('ToDos') ? JSON.parse(localStorage.getItem('ToDos')) : []);
  const [inputToDoName, setInputToDoName] = useState('');
  const refTest = useRef(null);
  // DAy JS /DATE FNS
  console.log(refTest);
  const [coordinate, setCoordinate] = useState(0);
  return (
    <div className={style.main}>
      <header>
        <h1>ToDo list</h1>
      </header>
      <body
        className={style.authorized}
      >
        <div className={style.test}>
          <div
            className={style.circle}
            style={{
              left: `${coordinate === 0 ? 0 : coordinate === 1 ? '35%' : '71%'}`,
              background: `${coordinate === 0 ? 'rgba(0, 0, 0, 0.2)' : coordinate === 1 ? 'rgba(143, 214, 243, 0.74)' : 'rgba(164, 253, 164, 0.74)'}`,
            }}
          />
          <span
            role="presentation"
            onClick={() => setCoordinate(0)}
            ref={refTest}
          >
            111111
          </span>
          <span
            role="presentation"
            onClick={() => setCoordinate(1)}
          >
            222222
          </span>
          <span
            role="presentation"
            onClick={() => setCoordinate(2)}
          >
            333333
          </span>
        </div>
        <div className={style.authorizedBlock}>
          <div className={style.createToDoBLock}>
            <CreateToDo
              inputToDoName={inputToDoName}
              setInputToDoName={setInputToDoName}
              toDoList={toDoList}
            />
          </div>
          {toDoList.map(e => (
            <ToDo
              e={e}
              toDoList={toDoList}
              setToDoList={setToDoList}
            />
          ))}
        </div>
      </body>
    </div>
  );
};

export default App;
