import React, { useEffect, useRef, useState } from 'react';

import styles from './styles.module.scss';

const Hint = ({
  title,
  description,
  children,
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [childPosition, setChildPosition] = useState({});
  const childRef = useRef();
  console.log([childPosition, childRef]);
  // const setHintPosition = () => ({
  //   left: childRef.current.offsetLeft,
  //   top: childRef.current.offsetTop,
  // });

  const showHint = () => {
    setIsVisible(true);
  };
  const hideHint = () => {
    setIsVisible(false);
  };

  useEffect(() => {
    if (childRef.current) {
      setChildPosition(childRef.current.getBoundingClientRect());
    }
  }, []);

  return (
    <div
      className={styles.mainBlock}
      onMouseEnter={showHint}
      onMouseLeave={hideHint}
    >
      {React.cloneElement(children, { ref: childRef })}
      {isVisible && (
        <div
          className={styles.tooltipBlock}
          style={{ left: 0 }}
          // style={setHintPosition()}
        >
          <h3>{title}</h3>
          <span>{description}</span>
        </div>
      )}
    </div>
  );
};

export default Hint;
