import React, { useState } from 'react';

import style from './style.module.scss';

const App = () => {
  const [userData, setUserData] = useState({
    name: localStorage.getItem('userName') ? localStorage.getItem('userName') : '',
    toDosCount: localStorage.getItem('todosCount') ? localStorage.getItem('todosCount') : 0,
    toDos: [],
  });
  const [inputUserName, setInputUserName] = useState('');
  const [inputToDoName, setInputToDoName] = useState('');
  // const [toDoData, setToDoData] = useState({});
  const createUser = () => {
    if (inputUserName) {
      let userNameFirstLetterUpperCase = inputUserName;
      inputUserName.split('').forEach((e, i) => {
        if (i === 0 || inputUserName[i - 1] === ' ') {
          userNameFirstLetterUpperCase = userNameFirstLetterUpperCase.substring(0, i) + e.toUpperCase()
          + userNameFirstLetterUpperCase.substring(i + 1);
        }
      });
      localStorage.setItem('userName', userNameFirstLetterUpperCase);
      setUserData({
        name: userNameFirstLetterUpperCase,
        todosCount: userData.todosCount,
      });
    }
  };
  const createToDo = () => {
    let todo = {
      name: inputToDoName,
    };
    console.log(todo);
  };
  return (
    <div className={style.main}>
      <header>
        <h1>{`${userData.name} ToDo list`}</h1>
      </header>
      <body
        className={userData.name === ''
          ? style.unauthorized
          : style.authorized}
      >
        {userData.name === '' ? (
          <div className={style.unauthorizedBlock}>
            <form action="#">
              <input
                type="text"
                placeholder="Set username"
                value={inputUserName}
                required
                onChange={e => setInputUserName(e.target.value)}
              />
              <button
                onClick={createUser}
                type="submit"
              >
                Submit
              </button>
            </form>
          </div>
        ) : (
          <div className={style.authorizedBlock}>
            <div className={style.createToDoBLock}>
              <form action="#">
                <input
                  type="text"
                  placeholder="Write what to do"
                  value={inputUserName}
                  required
                  onChange={e => setInputToDoName(e.target.value)}
                />
                <button
                  onClick={createToDo}
                  type="submit"
                >
                  Create
                </button>
              </form>
            </div>
          </div>
        )}
      </body>
    </div>
  );
};

export default App;
