import React, { useState, useEffect } from 'react';

import styles from './styles.module.scss';

const Hint = ({
  title,
  description,
  children,
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [childRef, setChildRef] = useState({});

  useEffect(() => {
    setChildRef(React.createRef());
    // setChildRefs(Array(children.length).fill().map((_, i) => childRefs[i] || React.createRef()));
  }, [children]);
  console.log(childRef);
  const showHint = () => {
    setIsVisible(true);
    // const childPositions = childRefs.map(ref => ref.current.getBoundingClientRect());
    // console.log(childPositions);
  };

  const hideHint = () => {
    setIsVisible(false);
  };
  return (
    <div
      className={styles.mainBlock}
      onMouseEnter={showHint}
      onMouseLeave={hideHint}
    >
      {React.Children.map(children, (child, i) => React.cloneElement(child, { ref: childRef[i] }))}
      {isVisible && (
        <div className={styles.tooltipBlock}>
          <h3>{title}</h3>
          <span>{description}</span>
        </div>
      )}
    </div>
  );
};

export default Hint;
