import React, { useState } from 'react';

import style from './style.module.scss';
import CreateToDo from './components/CreateToDo';
import ToDo from './components/ToDo';

const App = () => {
  const [toDoList, setToDoList] = useState(localStorage.getItem('ToDos') ? JSON.parse(localStorage.getItem('ToDos')) : []);
  const [inputToDoName, setInputToDoName] = useState('');
  // const createUser = () => {
  //   if (inputUserName) {
  //     let userNameFirstLetterUpperCase = inputUserName;
  //     inputUserName.split('').forEach((e, i) => {
  //       if (i === 0 || inputUserName[i - 1] === ' ') {
  //         userNameFirstLetterUpperCase = userNameFirstLetterUpperCase.substring(0, i) + e.toUpperCase()
  //         + userNameFirstLetterUpperCase.substring(i + 1);
  //       }
  //     });
  //     localStorage.setItem('userName', userNameFirstLetterUpperCase);
  //     setUserData({
  //       name: userNameFirstLetterUpperCase,
  //       todosCount: userData.todosCount,
  //       toDos: userData.toDos,
  //     });
  //   }
  // };

  // DAy JS /DATE FNS
  const createToDo = () => {
    if (inputToDoName) {
      const date = new Date();
      const todo = {
        name: inputToDoName,
        time: `${date.getHours()}:${date.getMinutes() > 10
          ? date.getMinutes()
          : `0${date.getMinutes()}`}`,
        id: (new Date().getTime()),
        description: '',
      };
      toDoList.push(todo);
      localStorage.setItem('ToDos', JSON.stringify(toDoList));
      setInputToDoName('');
    }
  };
  const deleteToDo = (e) => {
    setToDoList(toDoList.filter(todo => todo.id !== e.id));
  };

  // const setToDoDescription = (value, element) => {
  //   userData.toDos.forEach((e, i) => {
  //     console.log(userData.toDos[i]);
  //
  //   });
  // };
  return (
    <div className={style.main}>
      <header>
        <h1>ToDo list</h1>
      </header>
      <body
        className={style.authorized}
      >
        <div className={style.authorizedBlock}>
          <div className={style.createToDoBLock}>
            <CreateToDo
              inputToDoName={inputToDoName}
              setInputToDoName={setInputToDoName}
              createToDo={createToDo}
            />
          </div>
          {toDoList.map(e => (
            <ToDo e={e} deleteToDo={deleteToDo} />
          ))}
        </div>
      </body>
    </div>
  );
};

export default App;
