import React, { useState } from 'react';

import style from './style.module.scss';

const App = () => {
  const [userData, setUserData] = useState({
    name: localStorage.getItem('userName') ? localStorage.getItem('userName') : '',
    todosCount: localStorage.getItem('todosCount') ? localStorage.getItem('todosCount') : 0,
  });
  const [inputUserName, setInputUserName] = useState('');
  const createUser = () => {
    const userName = inputUserName.split('');
    const upperCaseFirstLetter = [];
    upperCaseFirstLetter.push(userName[0].toUpperCase());
    for (let i = 1; i <= userName.length - 1; i += 1) {
      if (userName[i] === ' ' && typeof userName[i + 1] !== 'number') {
        upperCaseFirstLetter.push(userName[i + 1].toUpperCase());
        return;
      }
      upperCaseFirstLetter.push(userName[i]);
    }
    console.log(upperCaseFirstLetter);
    setUserData({
      name: upperCaseFirstLetter.join(''),
      todosCount: userData.todosCount,
    });
  };
  return (
    <div className={style.main}>
      <header>
        <h1>{`${userData.name} ToDo list`}</h1>
      </header>
      <body>
        <div className={style.authorize}>
          <input
            type="text"
            placeholder="Set username"
            value={inputUserName}
            onChange={e => setInputUserName(e.target.value)}
          />
          <button
            onClick={createUser}
            type="button"
          >
            Submit
          </button>
        </div>
      </body>
    </div>
  );
};

export default App;
