import React from 'react';

import style from './style.module.scss';

const ToDo = ({ e, deleteToDo }) => (
  <div className={style.toDo}>
    <h4>{e.name}</h4>
    <textarea
      name="description"
      id={e.id}
      cols="30"
      rows="10"
      placeholder="description"
      // onChange={value => setToDoDescription(value, e)}
    />
    <span>{e.time}</span>
    <button
      type="button"
      onClick={() => deleteToDo(e)}
    >
      X
    </button>
  </div>
);

export default ToDo;
