import React from 'react';

import style from './style.module.scss';

const Background = () => {
  const blick = Array(10).fill(null);
  console.log(blick);
  const moveBlock = (n) => {
    setInterval(() => Math.floor(Math.random() * n) + 1, 300);
  };
  console.log(moveBlock(window.innerHeight));
  return (
    <div className={style.main}>
      {blick.map(() => (
        <div
          className={style.blick}
          style={{
            left: Math.floor(Math.random() * window.innerWidth) + 1,
            top: Math.floor(Math.random() * window.innerHeight) + 1,
          }}
        />
      ))}
    </div>
  );
};

export default Background;
