import React from 'react';

import styles from './styles.module.scss';

const Hint = ({ tittle, description, showHint }) => {
  // const [data, setData] = useState({});
  console.log('HINT', styles);

  return (
    showHint && (
      <div
        className={styles.mainBlock}
      >
        <h3>{tittle}</h3>
        <span>{description}</span>
      </div>
    )
  );
};

export default Hint;
