import React from 'react';

import style from './style.module.scss';

const ToDo = ({ e, toDoList, setToDoList }) => {
  const deleteToDo = () => {
    setToDoList(toDoList.filter(todo => todo.id !== e.id));
  };
  return (
    <div className={style.toDo}>
      <div className={style.hoverZone} />
      <input type="checkbox" />
      <div className={style.toDoTittle}>
        <span>{e.name}</span>
      </div>
      <div className={style.toDoTime}>
        <span>{e.time}</span>
      </div>
      <button
        type="button"
        onClick={() => deleteToDo(e)}
      >
        X
      </button>
    </div>
  );
};

export default ToDo;
