import React from 'react';

import style from './style.module.scss';

const CreateToDo = ({ inputToDoName, setInputToDoName, toDoList }) => {
  const createToDo = () => {
    if (inputToDoName) {
      const date = new Date();
      const todo = {
        name: inputToDoName,
        time: `${date.getHours()}:${date.getMinutes() > 10
          ? date.getMinutes()
          : `0${date.getMinutes()}`}`,
        id: (new Date().getTime()),
        description: '',
      };
      toDoList.push(todo);
      localStorage.setItem('ToDos', JSON.stringify(toDoList));
      setInputToDoName('');
    }
  };

  return (
    <div className={style.main}>
      <form action="#">
        <input
          type="text"
          placeholder="Write what to do"
          value={inputToDoName}
          required
          onChange={e => setInputToDoName(e.target.value)}
        />
        <button
          onClick={createToDo}
          type="submit"
        >
          Create
        </button>
      </form>
    </div>
  );
};

export default CreateToDo;
